import { createSlice } from "@reduxjs/toolkit";

const initialCartState = {
    info: [],
    isCartUp: false
};

const cartSlice = createSlice({
    name: 'cart',
    initialState: initialCartState,
    reducers: {
        handleUpdateItem(state, action) {
            const prevState = [...state.info.map(item => ({ ...item }))];
            const index = prevState.findIndex((item) => item.title === action.payload.title);
            if (index > -1) {
                prevState[index].qnt++;
            }
            else {
                prevState.push(action.payload);
            }

            state.info = prevState;
        },
        handleToggle(state) {
            state.isCartUp = !state.isCartUp;
        }
    }
});

export default cartSlice;
